
public class String5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s1 = "   Hello   ";
		System.out.println(s1);
		System.out.println(s1.length());

		String s2 = s1.trim();
		System.out.println(s2);
		System.out.println(s2.length());

		System.out.println(s1);

	}

}
